import React, { Component } from 'react';
import "./Css/ForgotPassword.css" ;
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import forgetPassword from '.';


class ForgotPassword extends Component {

    constructor(props) {
        super(props);

        this.state = {
            showPassword: false,
            email: "",
            errors: {},
        };
    }

    handleChangeText = (event) => {

        this.setState({
            [event.target.name]: event.target.value
        }, () => console.log(this.state, '------>name'))
    }

    submitForgetPasswordForm = () => {
        // if (this.validateForm()) {
            let user = {};
            user.email = this.state.email;
            user.password = this.state.password;
            login(user)
                .then(Response => {


                    console.log(Response , "Login success")
                    alert(`Login Successfull!!`);

                })
                .catch(err => {
                    console.log(Response, "user login fail");
                    alert(`Login Failed`);
                });
        // }
    }




    render() {
        return (
            <div className ="ForgotPassword">

                <div className="fundooLogo">
                    <span style={{ color: "blue" }}>F</span>
                    <span style={{ color: "red" }}>U</span>
                    <span style={{ color: "orange" }}>N</span>
                    <span style={{ color: "blue" }}>D</span>
                    <span style={{ color: "green" }}>O</span>
                    <span style={{ color: "red" }}>O</span>
                </div>
                <div className="ForgotPasswordtitle">ForgotPassword</div>

                <div className="emailtext">
                    <TextField id="outlined-search" margin="dense"
                        size="small"
                        label="Enter the Email id"
                        type="search"
                        variant="outlined"
                        name="email"
                        onChange={this.handleChangeText}
                        error={this.state.errors.email}
                        helperText={this.state.errors.email}
                    />
                </div>
                <div className="forgotbutton">
                    <Button variant="contained" color="secondary" onClick={this.submitForgetPasswordForm}>
                        Submit
                    </Button>
                    <Button variant="contained" color="secondary" onClick={this.handlecancel}>
                        Cancel
                  </Button>
                </div>


            </div>
        );
    }
}

export default ForgotPassword;
