import React, { Component } from 'react';
import './Css/Login.css';
import TextField from "@material-ui/core/TextField";
import IconButton from "@material-ui/core/IconButton";
import InputAdornment from "@material-ui/core/InputAdornment";
import Visibility from "@material-ui/icons/Visibility";
import VisibilityOff from "@material-ui/icons/VisibilityOff";
import Button from '@material-ui/core/Button' ;
import {login} from '../'



export default class Login extends Component {
    constructor(props) {
        super(props);

        this.state = {
            showPassword: false,
            userName: "",
            userPassword: "",
            email: "",
            password: "",
            isValid: true,
            errors: {}
        };

    }

    handleChangeText = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        }, () => console.log(this.state, 'data'))
    }

    submitUserSignInForm = () => {
        if (this.validateForm()) {
            let user = {};
            user.email = this.state.email;
            user.password = this.state.password;
            userLogin(user)
                .then(Response => {

                    this.props.history.push("/register");
                    console.log("Res", Response)
                    alert(`Login Successfull!!`);

                })
                .catch(err => {
                    console.log(Response, "user login fail");
                    alert(err);
                });
        }
    }


    render() {
        return (
            <div className="loginPage">


                <div className ="FundooLogo">
                    <span style={{ color: "blue" }}>F</span>
                    <span style={{ color: "red" }}>U</span>
                    <span style={{ color: "orange" }}>N</span>
                    <span style={{ color: "blue" }}>D</span>
                    <span style={{ color: "green" }}>O</span>
                    <span style={{ color: "red" }}>O</span>
                </div>
                <div className ="signInLogo" >
                    <span>Login </span>
                </div>


                <div className="usernameText">

                    <TextField
                        margin="dense"
                        size="small"
                        name="email"
                        id="outlined-full-width"
                        label="username"
                        variant="outlined"

                    />
                </div>
                <br></br>
                <div className="passwordText">
                    <TextField

                        style={{ width: "100%" }}
                        size="small"
                        margin="dense"
                        id="outlined-adornment-password"
                        variant="outlined"
                        name="password"
                        type={this.state.showPassword ? "text" : "password"}
                        label="password"
                        margin="dense"
                        onChange={this.handleChangeText}

                        InputProps={{
                            endAdornment: (
                                <InputAdornment position="end" sytle={{ width: "1px" }}>
                                    <IconButton
                                        onClick={
                                            () => this.setState({ showPassword: !this.state.showPassword })
                                        }
                                    >
                                        {this.state.showPassword ? <Visibility /> : <VisibilityOff />}
                                    </IconButton>

                                </InputAdornment>
                            )
                        }}
                    />

                </div>


                <div className="ButtonsLoginRegister">
                    <Button variant="outlined"  color="secondary" >  Register   </Button>

                    <Button  variant="outlined"  color="secondary" >  Login  </Button>

                </div>
                <div className="Forgotpassword">

                    <Button  style={{color: '#9e2059' }}>Forgot Password?</Button>

                </div>
            </div>



        );
    }
}
